-- 4. Implemente uma procedure para excluir um autor e remover sua associação com os livros na tabela "LivroAutor".CREATE PROCEDURE usp_ExcluirAuror @nome VARCHAR(100)ASBEGIN	DECLARE @id INT	SELECT @id = A.id	FROM Autor AS A	WHERE A.nome = @nome	DELETE FROM LivroAutor WHERE fk_autor = @id	DELETE FROM Autor WHERE nome = @nomeENDEXEC usp_ExcluirAuror 'J.K. Rowling';SELECT * FROM AutorSELECT * FROM LivroAutor