-- 6. Desenvolva uma procedure que receba o nome de um autor e retorne todos os livros escritos por esse autor.CREATE PROCEDURE usp_LivrosAutor (@nome VARCHAR(100))ASBEGIN	SELECT L.isbn, L.titulo, L.ano, A.nome	FROM Livro AS L	INNER JOIN LivroAutor AS LA	ON L.isbn = LA.fk_livro	INNER JOIN AUTOR AS A	ON LA.fk_autor = A.id	WHERE A.nome = @nomeENDEXEC usp_LivrosAutor 'Affonso Solano'; 