-- 1. Crie uma procedure que permita inserir uma nova categoria na tabela "Categoria".CREATE PROCEDURE usp_InserirCategoria    @tipo_categoria VARCHAR(50)ASBEGIN     insert into categoria (tipo_categoria) values (@tipo_categoria);ENDEXEC usp_InserirCategoria 'Categoria Nova';GO-- 2. Crie uma procedure para atualizar os detalhes de um livro (por exemplo, título, ano) pelo ISBN.CREATE PROCEDURE usp_DetalhesLivro (@isbn VARCHAR(50))ASBEGIN	SELECT *	FROM Livro AS L	WHERE L.isbn = @isbnENDEXEC usp_DetalhesLivro '8532511015';GO-- 3. Desenvolva uma procedure para adicionar um novo autor à tabela "Autor".CREATE PROCEDURE usp_InserirAutor    @nome VARCHAR(100),    @nacionalidade VARCHAR(50)ASBEGIN     insert into autor (nome, nacionalidade) values (@nome, @nacionalidade);ENDEXEC usp_InserirAutor 'Luiza', 'Brasileira';GO-- 4. Implemente uma procedure para excluir um autor e remover sua associação com os livros na tabela "LivroAutor".CREATE PROCEDURE usp_ExcluirAuror @nome VARCHAR(100)ASBEGIN	DECLARE @id INT	SELECT @id = A.id	FROM Autor AS A	WHERE A.nome = @nome	DELETE FROM LivroAutor WHERE fk_autor = @id	DELETE FROM Autor WHERE nome = @nomeENDEXEC usp_ExcluirAuror 'J.K. Rowling';SELECT * FROM AutorSELECT * FROM LivroAutorGO-- 5. Crie uma procedure que receba o nome de uma categoria e retorne todos os livros dentro dessa categoria.CREATE PROCEDURE usp_LivrosCategoria (@tipo_categoria VARCHAR(50))ASBEGIN	SELECT *	FROM Livro AS L	INNER JOIN Categoria AS C	ON C.id = L.fk_categoria	WHERE C.tipo_categoria = @tipo_categoriaENDEXEC usp_LivrosCategoria 'Humor';GO-- 6. Desenvolva uma procedure que receba o nome de um autor e retorne todos os livros escritos por esse autor.CREATE PROCEDURE usp_LivrosAutor (@nome VARCHAR(100))ASBEGIN	SELECT L.isbn, L.titulo, L.ano, A.nome	FROM Livro AS L	INNER JOIN LivroAutor AS LA	ON L.isbn = LA.fk_livro	INNER JOIN AUTOR AS A	ON LA.fk_autor = A.id	WHERE A.nome = @nomeENDEXEC usp_LivrosAutor 'Affonso Solano'; GO-- 7. Crie uma procedure que liste os livros publicados em um ano específico.CREATE PROCEDURE usp_LivroAno (@ano INT)ASBEGIN	SELECT * 	FROM LIVRO AS L	WHERE L.ano = @anoENDEXEC usp_LivroAno 2000;GO-- 8. Implemente uma procedure para listar os livros publicados por uma editora específica.CREATE PROCEDURE usp_LivrosEditora (@editora VARCHAR(100))ASBEGIN	SELECT L.isbn, L.titulo, L.ano, E.nome	FROM Livro AS L	INNER JOIN Editora AS E	ON L.fk_editora = E.id	WHERE E.nome = @editoraENDEXEC usp_LivrosEditora 'Rocco';GO-- 10. Crie uma procedure para contar o número de livros em cada categoria.CREATE PROCEDURE usp_ContarLivrosASBEGIN	SELECT COUNT(*) AS Quantidade, C.tipo_categoria	FROM Livro AS L	INNER JOIN Categoria AS C	ON L.fk_categoria = C.id	GROUP BY C.tipo_categoriaENDEXEC usp_ContarLivros;GO-- 11. Implemente uma procedure para listar os livros por autores de uma nacionalidade específica.CREATE PROCEDURE usp_LivrosNacionalidade (@nacionalidade VARCHAR(50))ASBEGIN	SELECT L.isbn, L.titulo, L.ano, A.nome, A.nacionalidade	FROM Livro AS L	INNER JOIN LivroAutor AS LA	ON L.isbn = LA.fk_livro	INNER JOIN AUTOR AS A	ON LA.fk_autor = A.id	WHERE A.nacionalidade = @nacionalidadeENDEXEC usp_LivrosNacionalidade 'Brasil'